#!/usr/bin/env python3\n\"\"\"\n간단한 FastAPI 서버 테스트\n기본적인 의존성과 설정만 확인\n\"\"\"\n\ntry:\n    print(\"=== AIMEX 서버 테스트 시작 ===\")\n    \n    # 1. 기본 패키지 확인\n    print(\"\\n1. 기본 패키지 테스트...\")\n    import fastapi\n    import uvicorn\n    print(f\"   ✅ FastAPI {fastapi.__version__}\")\n    print(f\"   ✅ Uvicorn {uvicorn.__version__}\")\n    \n    # 2. SQLAlchemy 확인\n    print(\"\\n2. 데이터베이스 패키지 테스트...\")\n    import sqlalchemy\n    import pymysql\n    print(f\"   ✅ SQLAlchemy {sqlalchemy.__version__}\")\n    print(f\"   ✅ PyMySQL {pymysql.__version__}\")\n    \n    # 3. 기본 FastAPI 앱 생성 테스트\n    print(\"\\n3. FastAPI 앱 생성 테스트...\")\n    from fastapi import FastAPI\n    test_app = FastAPI(title=\"Test App\")\n    \n    @test_app.get(\"/\")\n    def read_root():\n        return {\"message\": \"AIMEX Test Server\", \"status\": \"OK\"}\n    \n    print(\"   ✅ FastAPI 앱 생성 성공\")\n    \n    # 4. 환경변수 로드 테스트\n    print(\"\\n4. 환경변수 로드 테스트...\")\n    from dotenv import load_dotenv\n    import os\n    load_dotenv()\n    \n    # 백엔드 디렉토리의 .env 파일 로드\n    env_path = os.path.join(\"backend\", \".env\")\n    if os.path.exists(env_path):\n        load_dotenv(env_path)\n        print(\"   ✅ 환경변수 파일 로드 성공\")\n        \n        # 중요한 환경변수들 확인\n        db_url = os.getenv(\"DATABASE_URL\", \"None\")\n        secret_key = os.getenv(\"SECRET_KEY\", \"None\")\n        \n        print(f\"   📊 DATABASE_URL: {db_url[:30]}...\")\n        print(f\"   🔐 SECRET_KEY: {'설정됨' if secret_key != 'None' else '미설정'}\")\n    else:\n        print(\"   ⚠️  환경변수 파일을 찾을 수 없습니다\")\n    \n    print(\"\\n🎉 기본 테스트 모두 통과!\")\n    print(\"\\n📋 다음 단계:\")\n    print(\"   1. 'cd backend' 로 백엔드 디렉토리 이동\")\n    print(\"   2. 'python run.py' 로 서버 실행\")\n    print(\"   3. 브라우저에서 http://localhost:8000 접속\")\n    \nexcept ImportError as e:\n    print(f\"❌ 패키지 임포트 오류: {e}\")\n    print(\"   해결방법: pip install -r backend/requirements.txt\")\nexcept Exception as e:\n    print(f\"❌ 예상치 못한 오류: {e}\")\n    import traceback\n    traceback.print_exc()\n