#!/bin/bash\n\n# ===================================================\n# AIMEX 프로젝트 실행 스크립트\n# Clean Architecture & SOLID 원칙 적용 완료\n# ===================================================\n\necho \"🚀 AIMEX 프로젝트 실행 가이드\"\necho \"========================================\"\n\n# 프로젝트 디렉토리로 이동\necho \"📍 프로젝트 디렉토리로 이동...\"\ncd \"C:/encore-skn11/SKN/SKN-FINAL\"\npwd\n\necho \"\"\necho \"📋 실행 단계:\"\necho \"1. 데이터베이스 설정\"\necho \"2. 백엔드 서버 실행\"\necho \"3. 프론트엔드 서버 실행\"\necho \"4. 통합 테스트\"\n\necho \"\"\necho \"⚠️  사전 요구사항 확인:\"\necho \"- MySQL 8.0+ 서버 실행 중\"\necho \"- Python 3.9+ 설치\"\necho \"- Node.js 18+ 설치\"\necho \"\"\n\nread -p \"계속 진행하시겠습니까? (y/n): \" -n 1 -r\necho\nif [[ ! $REPLY =~ ^[Yy]$ ]]; then\n    echo \"❌ 실행이 취소되었습니다.\"\n    exit 1\nfi\n\necho \"\"\necho \"🔧 1단계: 데이터베이스 설정\"\necho \"========================================\"\n\necho \"📊 기본 패키지 테스트...\"\npython quick_test.py\n\nif [ $? -eq 0 ]; then\n    echo \"✅ 기본 패키지 테스트 성공\"\nelse\n    echo \"❌ 기본 패키지 테스트 실패\"\n    echo \"해결방법: pip install -r backend/requirements.txt\"\n    exit 1\nfi\n\necho \"\"\necho \"🗃️  데이터베이스 초기화...\"\npython init_database.py init\n\nif [ $? -eq 0 ]; then\n    echo \"✅ 데이터베이스 초기화 성공\"\nelse\n    echo \"❌ 데이터베이스 초기화 실패\"\n    echo \"해결방법:\"\n    echo \"1. MySQL 서버 실행 상태 확인\"\n    echo \"2. backend/.env 파일의 DATABASE_URL 확인\"\n    exit 1\nfi\n\necho \"\"\necho \"🌱 시드 데이터 생성...\"\npython create_seed_data.py\n\nif [ $? -eq 0 ]; then\n    echo \"✅ 시드 데이터 생성 성공\"\nelse\n    echo \"❌ 시드 데이터 생성 실패\"\n    exit 1\nfi\n\necho \"\"\necho \"🎉 1단계 완료! 데이터베이스 설정이 완료되었습니다.\"\necho \"\"\necho \"📋 다음 단계 실행 명령어:\"\necho \"\"\necho \"2단계: 백엔드 서버 실행\"\necho \"cd backend\"\necho \"pip install -r requirements.txt\"\necho \"python run.py\"\necho \"\"\necho \"3단계: 프론트엔드 서버 실행 (새 터미널)\"\necho \"cd frontend\"\necho \"npm install\"\necho \"npm run dev\"\necho \"\"\necho \"4단계: 통합 테스트\"\necho \"- 프론트엔드: http://localhost:3000\"\necho \"- API 문서: http://localhost:8000/docs\"\necho \"- 서버 상태: http://localhost:8000/health\"\necho \"\"\necho \"🎯 모든 단계가 성공하면 AIMEX 시스템이 완전히 실행됩니다!\"\n